      # Foo 子系统/驱动程序（注释...）

	  INTERFACE foo

       	METHOD int doit {
       		device_t dev;
       	};

       	# 如果没有通过DEVMETHOD()提供一个方法，则DEFAULT为将会被使用的方法

       	METHOD void doit_to_child {
       		device_t dev;
       		driver_t child;
       	} DEFAULT doit_generic_to_child;

figure.programlisting:
struct driver {
   	KOBJ_CLASS_FIELDS;
    	void	*priv;			/* 驱动程序私有数据 */
};

/*
 * src/sys/sys/bus.h
 */
typedef enum device_state {
	DS_NOTPRESENT,	/* 未探测或探测失败 */
  	DS_ALIVE,		/* 探测成功 */
  	DS_ATTACHED,	/* 调用了连接方法 */
  	DS_BUSY			/* 设备已打开 */
} device_state_t;
